import React from 'react'
import RecipeFinder from '../api/RecipeFinder';
import { useParams } from 'react-router-dom'


const Ingredients = ({ ingredients }) => {
    const id = useParams()

    const handleDelete = async (e, el) => {
        // console.log(`I want to delete, ${el.id}`)
        e.preventDefault();
        try {
            await RecipeFinder.delete(`/api/v1/recipes/${id}/ingredients`, el.id)
            console.log(el.id)
        } catch (err) { console.log(err) }
    }
    return (
        <div>
            {ingredients.map((el) => {
                return (
                    < div key={el.id}>
                        {console.log(el)}
                        <div className="card-header d-flex justify-content-between">
                            <span>{el.ingredients}</span>
                            <span><button onClick={(e) => handleDelete(e, el)} >Delete</button></span>

                        </div>
                    </div>
                )
            })}
        </div>
    )
}

export default Ingredients

