import React from 'react'
import RecipeFinder from '../api/RecipeFinder';

const Ingredients = ({ ingredients }) => {

    const handleDelete = async (e, id) => {
        // console.log(`I want to delete, ${id}`)
        e.preventDefault();
        try {
            await RecipeFinder.delete(`/${id}/ingredients`, id)
        } catch (err) { console.log(err) }
    }
    return (
        <div>
            {ingredients.map((el) => {
                return (
                    < div key={el.id}>
                        {/* {console.log(el)} */}
                        <div className="card-header d-flex justify-content-between">
                            <span>{el.ingredients}</span>
                            <span><button onClick={(e) => handleDelete(e, el.id)} >Delete</button></span>

                        </div>
                    </div>
                )
            })}
        </div>
    )
}

export default Ingredients

